INSERT INTO public.topic (id, created_at, updated_at, session_id, subject, description, speaker, location) VALUES ('d92f1829-4b52-46ec-ad2d-de3d8d02b629', '2020-07-25 11:51:32.602618+00', '2020-07-25 11:51:32.602618+00', 'deb574ee-91b2-47e7-b7b2-fb357cac8e56', 'Ślepa wiara', 'Bez wiary nie można podobać się Bogu, a zatem, jak być człowiekiem wiary? Jak żyć wiarą i nie dać się zniechęcić? Jak zrobić kroki, które nie wiesz, dokąd Cię zaprowadzą? Jak wyjść na głęboka wodę, do miejsc, które przerastają twoje wyobrażenia? Na tym wykładzie poszerzysz swoją wizję i rozciągniesz swoją wiarę.', 'Julita Rachwalska', 'sala 2');
